import React, { useState } from "react";
import styles from "./styles.module.css";
import jsonData from "./components/productData.json";

function App() {
  const [searchTerm, setSearchTerm] = useState("");

  return (
    <div className={styles.main}>
      <input
        type="text"
        onChange={(event) => {
          setSearchTerm(event.target.value);
        }}
      />
      {jsonData.filter(value => {
        if (searchTerm == "") {
          return value
        } else if (value.ProductName.toLowerCase().includes(searchTerm.to)) {
          return value
        }
      }).map((value, key) => {
        return (
          <div>
            <img src={value.Image}></img>
            <h2>{value.ProductName}</h2>
            <h3>{value.Price}</h3>
          </div>
        );
      })}
    </div>
  );
}

export default App;
