import React, { useState } from "react";
import styles from "./styles.module.css";
import jsonData from "./productData.json";

function SearchView(props) {
  return (
    <div className={styles2.SearchView}>
      {props.restaurants.map((restaurants) => (
        <SearchResult key={restaurants.id} {...restaurants} />
      ))}
    </div>
  );
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: jsonData.products,
      SearchString: "",
    };
  }

  onSearchChange = (event) => {
    this.setState({ SearchString: event.target.value });
  };

  render() {
    return (
      <div className={styles.main}>
        <input
          class="SearchBox"
          type="text"
          onChange={this.onSearchChange}
          value={this.state.SearchString}
          placeholder="Search for restaurants..."
        />
        <div>
          <SearchView
            products={this.state.products.filter((product) =>
              product.productName.toLowerCase().includes(this.state.SearchString.toLowerCase())
            )}
          />
        </div>
      </div>
    );
  }
}
export default app;

